<?phpfunction uploadfile( $prefname='', $tmp_path ='tmp2011' ){	global $nmFileAsli, $nmNewFile,$Main;		$errMaxFileSize = "Ukuran File Terlalu besar! Maksimum 50 Mb";	$maxFileSize = 50000000;	//$dirPath = 'dokum';	//$tmp_path ='tmp';	$tnmfile=$Main->DEF_PROPINSI.$Main->DEF_WILAYAH.'_';		$tnmfile=$Main->DEF_PROPINSI.$Main->DEF_WILAYAH.'_';	$errmsg='';	if ($_FILES['uploadedfile']['name'] != '') {		$file_size=$_FILES['uploadedfile']['size']; 		if($file_size >= $maxFileSize){	$errmsg= $errMaxFileSize; }						if($errmsg=='') {						$nmFileAsli = $_FILES['uploadedfile']['name'];								//generate new file name			$ext = pathinfo($_FILES['uploadedfile']['name'],PATHINFO_EXTENSION);								//*			$nmNewFile = $tnmfile.$prefname.( mt_rand()).'.'.$ext;										//copy to target path			$target_path= $tmp_path.'/';				$target_file = $target_path .$nmNewFile;	 			//*/						//$target_path= $tmp_path.'/';								//$target_file = tempnam($tmp_path, $prefname).'.'.$ext;			//$nmNewFile = pathinfo($target_file, PATHINFO_FILENAME);			if(move_uploaded_file($_FILES['uploadedfile']['tmp_name'], $target_file) == FALSE) {											$errmsg= 'There was an error uploading the file, please try again!';			}			}	}	return $errmsg;}function uploadfile2(  $maxFileSize=50000000, $ElNameUpload = 'uploadedfile', 		$prefname='', $path ='tmp2011', $errMaxFileSize = "Ukuran File Terlalu besar! Maksimum 50 Mb", $WithExt=TRUE ){	global $nmNewFile, $nmFileAsli, $errmsg,$Main;	//output	//echo"<br>ElNameUpload=$ElNameUpload";	$tnmfile=$Main->DEF_PROPINSI.$Main->DEF_WILAYAH.'_';	$sukses=FALSE;	if ($_FILES[$ElNameUpload]['name'] != '') {		$file_size=$_FILES[$ElNameUpload]['size']; 		if($file_size >= $maxFileSize){	$errmsg= $errMaxFileSize; }						if($errmsg=='') {						$nmFileAsli = $_FILES[$ElNameUpload]['name']; //echo"<br>nmFileAsli=$nmFileAsli";						//generate new file name			$ext = pathinfo($_FILES[$ElNameUpload]['name'],PATHINFO_EXTENSION);								if ($WithExt){				$nmNewFile = $tnmfile.date('Ymd').'_'.$prefname.( mt_rand()).'.'.$ext;	//echo"<br>nmNewFile=$nmNewFile";				}else{								$nmNewFile = $tnmfile.date('Ymd').'_'.$prefname.( mt_rand());			}						//$nmNewFile = $prefname.( mt_rand());						//copy to target file						$target_path = $path.'/';			$target_file = $target_path.$nmNewFile;	 			if(move_uploaded_file($_FILES[$ElNameUpload]['tmp_name'], $target_file) == FALSE) {											$errmsg= 'There was an error uploading the file, please try again!';			}else{				$sukses = TRUE;			}			}	}	return $sukses;}?>