<?phpinclude ('../../config.php');$usr1=$_GET['usr1'];//get list other user online $Kondisi = " where online='1' and lastaktif <>'' 				and uid <> '".$usr1."'				and TIMESTAMPDIFF(MINUTE,lastaktif,now()) < ".$USER_TIME_OUT;$Qry = mysql_query("select * from admin $Kondisi order by `group`");$hsl='';while ($isi=mysql_fetch_array($Qry)){	//get user info	$KODEPENGGUNA = "{$isi['uid']}";	$NAMAPENGGUNA = $isi["nama"];		$GROUP = $isi["group"];	$nmbid='';	$nmbid = table_get_value('select nm_skpd from ref_skpd 					where c="'.substr($GROUP,0,2).'" and d="00" and e="00"', 'nm_skpd');	$nmunit = table_get_value('select nm_skpd from ref_skpd 					where concat(c,".",d) = "'.substr($GROUP,0,5).'" and e="00" ','nm_skpd');	$nmsubunit = table_get_value('select nm_skpd from ref_skpd 					where concat(c,".",d,".",e) = "'.$GROUP.'"','nm_skpd');		if($nmbid!='' ){		$NMOPD = $nmbid.' - '.$nmunit.' - '.$nmsubunit;	}else{		$NMOPD = '-';	}		//create pesan	if ($hsl != ''){ $hsl .='<||>';} 	$hsl .= $KODEPENGGUNA.'<||>'.			$NAMAPENGGUNA.'<||>'.			$NMOPD;				 	}echo $hsl;?>