ix;			}function list_tablefooter($XXJML=0,$dlmribuan=''){$sharga = !empty($dlmribuan)?'TOTAL (Ribuan)':'TOTAL';$isix='<tr><td class="GarisCetak" colspan=12 ><b>'.$sharga.'</b></td><td class="GarisCetak" align=right><b><div class="numberfmt1">'.$XXJML.'</div></b></td><td class="GarisCetak" colspan=1 >&nbsp;</td></tr> 	</table>';return $isix;			}function list_footer($XXTMPTGL='',$XXJBT1='',$XXNM1='',$XXNIP1='',$XXJBT2='',$XXNM2='',$XXNIP2=''){$isix='<table style="width:30cm" border=0> 				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 >&nbsp;</td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 >&nbsp;</td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 style="font-weight:bold;font-size:10pt"><B>MENGETAHUI</B> </td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 style="font-weight:bold;font-size:10pt"><B>'.$XXTMPTGL.'</B> </td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 style="font-weight:bold;font-size:10pt"><B>'.$XXJABATAN1.'</B> </td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 style="font-weight:bold;font-size:10pt"><B>'.$XXJABATAN2.'</B> </td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 >&nbsp;</td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 >&nbsp;</td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 >&nbsp;</td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 >&nbsp;</td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=2 style="font-weight:bold;font-size:10pt"><B>( '.$XXNAMA1.' )</B> </td>				<td colspan=7 >&nbsp;</td> 				<td align=center colspan=3 style="font-weight:bold;font-size:10pt"><B>( '.$XXNAMA2.' )</B> </td>				<td >&nbsp;</td> 				</tr>				<tr> 				<td >&nbsp;</td> 				<td align=center colspan=