<?php//act ---------------------------------------------------------$ActEntry = $_POST['ActEntry']; echo "ActEntry = $ActEntry<br>";$fmIDBARANG = $_POST['fmIDBARANG'];$fmNMBARANG = $_POST['fmNMBARANG'];$fmNOREG = $_POST['fmNOREG'];//isian pemeliharaan ---------------$fmTANGGALPEMELIHARAAN = $_POST['fmTANGGALPEMELIHARAAN'] ;//'2010-02-25';$fmJENISPEMELIHARAAN = $_POST['fmJENISPEMELIHARAAN'] ;//'fmJENISPEMELIHARAAN';$fmPEMELIHARAINSTANSI = $_POST['fmPEMELIHARAINSTANSI'] ;//'fmPEMELIHARAINSTANSI';$fmPEMELIHARAALAMAT = $_POST['fmPEMELIHARAALAMAT'] ;//'fmPEMELIHARAALAMAT';$fmSURATNOMOR = $_POST['fmSURATNOMOR'] ;//'fmSURATNOMOR';$fmSURATTANGGAL = $_POST['fmSURATTANGGAL'] ;//'2010-02-15';$fmBIAYA = $_POST['fmBIAYA'] ;//'fmBIAYA';$fmBUKTIPEMELIHARAAN = $_POST['fmBUKTIPEMELIHARAAN'] ;//'fmBUKTIPEMELIHARAAN';$fmKET = $_POST['fmKET'] ;//'fmKET';//get state form -> edit/baru -----------------------------$stfm= $_GET['stfm'];  //echo "stfm = $stfm<br>";$idbi = $_GET['idbi']; //jika idbi ada nilai berarti baru//if ($idbi==''){	//$idbi = $_POST['$idbi'];	//echo "*idbi = $idbi<br>";//}$id= $_GET['id']; echo "id = $id<br>";//jika id pemeliharaan ada, berarti edit$AmbilData = $_POST['AmbilData'];if($stfm == 1){//baru	 	if (empty($idbi)){ $errmsg = 'ID Buku Induk tidak ada!'; $Info = "<script>alert('$errmsg')</script>";}		}else{//edit	// get data lama -------------------------------- 		if (empty($id)){ $errmsg = 'ID Pemeliharaan tidak ada!'; $Info = "<script>alert('$errmsg')</script>";}	//echo "$errmsg";	if ( !empty($id) && empty($AmbilData)  && $errmsg=='') {			Pelihara_GetData($id);		//$AmbilData = 1; //tanda sudah ambil data, biar ga 2x			}}//get info bi ---------------------------if (!empty($idbi) && $AmbilData != 1){	$isi = table_get_rec("select * from view_buku_induk2 where id = $idbi" );	if ($isi){		$fmIDBARANG = $isi['f'].'.'.$isi['g'].'.'.$isi['h'].'.'.$isi['i'].'.'.$isi['j'];//echo $fmIDBARANG;		$fmNMBARANG = $isi['nm_barang'];		